unset PACKAGES
unset SUDO
command -v tar >/dev/null 2>&1 || { PACKAGES=${PACKAGES}" tar"; }
command -v curl >/dev/null 2>&1 || { PACKAGES=${PACKAGES}" curl"; }
test "$(id -u)" = 0 || SUDO="sudo"

AGENT_BINARIES_URI=https://codenvy.com/update/repository/public/download/org.eclipse.che.ls.json.binaries
CHE_DIR=$HOME/che
LS_DIR=${CHE_DIR}/ls-json
LS_LAUNCHER=${LS_DIR}/launch.sh

LINUX_TYPE=$(cat /etc/os-release | grep ^ID= | tr '[:upper:]' '[:lower:]')
LINUX_VERSION=$(cat /etc/os-release | grep ^VERSION=)
MACHINE_TYPE=$(uname -m)

mkdir -p ${CHE_DIR}
mkdir -p ${LS_DIR}

########################
### Install packages ###
########################

# Red Hat Enterprise Linux 7
############################
if echo ${LINUX_TYPE} | grep -qi "rhel"; then
 test "${PACKAGES}" = "" || {
 ${SUDO} yum install ${PACKAGES};
 }


# Ubuntu 14.04 16.04 / Linux Mint 17
####################################
elif echo ${LINUX_TYPE} | grep -qi "ubuntu"; then
 test "${PACKAGES}" = "" || {
 ${SUDO} apt-get update;
 ${SUDO} apt-get -y install ${PACKAGES};
 }

 command -v nodejs >/dev/null 2>&1 || {
# {
# if test "${SUDO}" = ""; then
# curl -sL https://deb.nodesource.com/setup_5.x | bash -;
# else
# curl -sL https://deb.nodesource.com/setup_5.x | ${SUDO} -E bash -;
# fi
# };

 ${SUDO} apt-get update;
 ${SUDO} apt-get install -y nodejs;
 }


# Debian 8
##########
elif echo ${LINUX_TYPE} | grep -qi "debian"; then
 test "${PACKAGES}" = "" || {
 ${SUDO} apt-get update;
 ${SUDO} apt-get -y install ${PACKAGES};
 }

 command -v nodejs >/dev/null 2>&1 || {
 {
 if test "${SUDO}" = ""; then
 curl -sL https://deb.nodesource.com/setup_6.x | bash -;
 else
 curl -sL https://deb.nodesource.com/setup_6.x | ${SUDO} -E bash -;
 fi
 };

 ${SUDO} apt-get update;
 ${SUDO} apt-get install -y nodejs;
 }

# Fedora 23
###########
elif echo ${LINUX_TYPE} | grep -qi "fedora"; then
 PACKAGES=${PACKAGES}" procps-ng"
 test "${PACKAGES}" = "" || {
 ${SUDO} dnf -y install ${PACKAGES};
 }


# CentOS 7.1 & Oracle Linux 7.1
###############################
elif echo ${LINUX_TYPE} | grep -qi "centos"; then
 test "${PACKAGES}" = "" || {
 ${SUDO} yum -y install ${PACKAGES};
 }


# openSUSE 13.2
###############
elif echo ${LINUX_TYPE} | grep -qi "opensuse"; then
 test "${PACKAGES}" = "" || {
 ${SUDO} zypper install -y ${PACKAGES};
 }

else
 >&2 echo "Unrecognized Linux Type"
 >&2 cat /etc/os-release
 exit 1
fi


#####################
### Install C# LS ###
#####################

curl -s ${AGENT_BINARIES_URI} | tar xzf - -C ${LS_DIR}

######################
### Install NodeJS ###
######################

#if echo ${MACHINE_TYPE} | grep -qi "x86_64"; then
# NODEJS_VER=node-v5.0.0-linux-x64
#else
# NODEJS_VER=node-v5.0.0-linux-x86
#fi
#NODEJS_DIR=${CHE_DIR}/${NODEJS_VER}
#
#if [ ! -f "${NODEJS_DIR}/bin/node" ]; then
# curl -s -L https://nodejs.org/dist/v5.0.0/${NODEJS_VER}.tar.gz | tar xzf - -C ${CHE_DIR}
#fi

touch ${LS_LAUNCHER}
chmod +x ${LS_LAUNCHER}
echo "nodejs ${LS_DIR}/vscode-json-server/server.js" > ${LS_LAUNCHER}
